import * as types from './../constants/actionTypes';
let initialState = [
    {
        id: 1,
        name: 'Học Angular JS',
        status: true
    }
];

let myReducer = (state = initialState ,action) =>{
    switch(action.type){
        case types.LIST_ALL:
            return state;
        default: return state;
    }
    
}

export default myReducer;
